import React, { Component } from "react";
import ee from "@google/earthengine";
import CountrySelector from "./CountrySelector";

class Map extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedCountry: null,
      map: null,
      drawingManager: null,
      drawnShapes: [],
    };
    this.clearDrawing = this.clearDrawing.bind(this);
  }

  componentDidMount() {
    // Load the Google Maps API
    const script = document.createElement("script");
    script.src = `https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY&libraries=drawing&v=3.44`;
    script.async = true;
    document.body.appendChild(script);
    script.onload = () => {
      // Initialize the map and drawing manager
      this.initMap();
    };
  }

  initMap() {
    // Initialize the Google Map
    const map = new window.google.maps.Map(document.getElementById("map"), {
      center: { lat: 0, lng: 0 },
      zoom: 3,
    });
    this.setState({ map });

    // Initialize the drawing manager
    const drawingManager = new window.google.maps.drawing.DrawingManager({
      drawingMode: window.google.maps.drawing.OverlayType.RECTANGLE,
      drawingControl: true,
      drawingControlOptions: {
        position: window.google.maps.ControlPosition.TOP_CENTER,
        drawingModes: [window.google.maps.drawing.OverlayType.RECTANGLE],
      },
      rectangleOptions: {
        fillColor: "#00FF00",
        fillOpacity: 0.2,
        strokeWeight: 2,
        strokeColor: "#00FF00",
        clickable: true,
        editable: true,
        draggable: true,
      },
    });
    this.setState({ drawingManager });

    // Add the drawing manager to the map
    drawingManager.setMap(map);

    // Listen for rectangle complete event
    window.google.maps.event.addListener(
      drawingManager,
      "rectanglecomplete",
      (rectangle) => {
        // Get the rectangle's bounds and convert to Earth Engine geometry
        const bounds = rectangle.getBounds();
        const ne = bounds.getNorthEast();
        const sw = bounds.getSouthWest();
        const geometry = ee.Geometry.Rectangle([
          sw.lng(),
          sw.lat(),
          ne.lng(),
          ne.lat(),
        ]);

        // Load a Sentinel-2 image collection and add it to the map
        const collection = ee.ImageCollection("COPERNICUS/S2_SR")
          .filterBounds(geometry)
          .filterDate("2022-01-01", "2022-03-01")
          .median()
          .clip(geometry);

        const visParams = { bands: ["B4", "B3", "B2"], min: 0, max: 3000 };

        map.overlayMapTypes.insertAt(
          0,
          new ee.MapTypeOverlay({
            name: "Sentinel-2 SR",
            imageCollection: collection,
            visParams: visParams,
          })
        );

        // Add the drawn rectangle to the state
        const drawnShapes = [...this.state.drawnShapes, rectangle];
        this.setState({ drawnShapes });
      }
    );
  }

  clearDrawing() {
    // Loop through all the drawn shapes and remove them from the map
    const { drawnShapes } = this.state;
    drawnShapes.forEach((shape) => {
      shape.setMap(null);
    });

    // Clear the drawing manager's overlays array
    this.state.drawingManager.set("overlays", []);

    // Reset the drawing mode to rectangle
    this.state.drawingManager.setDrawingMode(
      window.google.maps.drawing.OverlayType.RECTANGLE
      );
    // Reset the state
    this.setState({
    selectedCountry: null,
    drawnShapes: [],
  });
}
render() {
    const { selectedCountry } = this.state;
    return (
    <div>
    {selectedCountry ? (
    <div>
    <h2>{selectedCountry.name}</h2>
    <div id="map" style={{ width: "100%", height: "500px" }}></div>
    <button onClick={this.clearDrawing}>Clear</button>
    </div>
    ) : (
    <div>
    <h2>Select a country</h2>
    <CountrySelector onCountrySelect={this.handleCountrySelect} />
    </div>
    )}
    </div>
    );
    }
}
export default Map;
